function showAlert(a,t,e){swal({icon:a,title:t,text:e})}function basicBarChart(a,t,e,s,i,n){new Chart(document.getElementById(a),{type:"bar",data:{labels:t,datasets:[{barPercentage:1,barThickness:18,maxBarThickness:100,minBarLength:14,label:e,type:"bar",backgroundColor:s,data:i}]},options:{title:{display:!0,text:n},legend:{display:!1}}})}function soldProductsChart(a,t){basicBarChart("topSales",a,"Top Vendas","#e93d58",t,"Top Vendas")}function topAfiliatesChart(a,t){basicBarChart("topAfiliates",a,"Top Afiliates","#926ee4",t,"Top Afiliates")}function salesProfitsByMonthsAndYear(a,t){basicBarChart("monthlyProfits",a,"Lucros Mensais","#e9643a",t,"Lucros Mensais")}function salesQuantitiesByMonthsAndYear(a,t){basicBarChart("monthlySalesQuantities",a,"Vendas Mensais","#3daee9",t,"quantitiesData")}